    member private x.getColor (value: float) (palette: string[]) = 
        let m = float (palette.Length - 1)
        let index = value |> max 0. |> min 1. |> (*) m |> floor |> int
        palette.[index]
    
    member x.color value =
        x.colors |> x.getColor value

